sys_variable_value.value:
(function execute(inputs, outputs) {
	var adbUtils = new AutonomousDatabaseSpokeUtils();
  	var payload = {};

    adbUtils.validateInputVariables(inputs, outputs);
    adbUtils.validateMandatoryVariables(inputs, ['database_id', 'display_name']);

  	payload.autonomousDatabaseId = inputs.database_id;
  	payload.displayName = inputs.display_name;
    outputs.payload = JSON.stringify(payload);
})(inputs, outputs);

