sys_variable_value.value:
(function execute(inputs, outputs) {
  if (inputs.status_code == "200") {
    try {
  	  var response_json = JSON.parse(inputs.response);
      outputs.status = "Success";
      outputs.database_id = response_json.autonomousDatabaseId;
      outputs.compartment_id = response_json.compartmentId;
      outputs.database_size_in_tbs = response_json.databaseSizeInTBs;
      outputs.display_name = response_json.displayName;
      outputs.backup_id = response_json.id;
      outputs.is_automatic = response_json.isAutomatic;
      outputs.is_restorable = response_json.isRestorable;
      outputs.lifecycle_details = response_json.lifecycleDetails;
      outputs.lifecycle_state = response_json.lifecycleState;
      outputs.time_ended = response_json.timeEnded;
      outputs.time_started = response_json.timeStarted;
      outputs.type = response_json.type;

    } catch (ex) {
      throw new Error('JSON Parse Error. Response Body may be empty.');
    }
  } else {
    var errMsgs = new AutonomousDatabaseErrorHandler().documentErrorHandler(inputs.response, inputs.status_code);
    outputs.error_message = errMsgs[0];
    outputs.status = errMsgs[1];
  }
})(inputs, outputs);

