sys_variable_value.value:
(function execute(inputs, outputs) {
	var adbUtils = new AutonomousDatabaseSpokeUtils();
    adbUtils.validateInputVariables(inputs, outputs);
    adbUtils.validateMandatoryVariables(inputs, ['database_id']);
    adbUtils.maptoOutputFromInput(inputs, outputs, ['database_id']);
})(inputs, outputs);

