sys_script_include.script:
var AutonomousDatabaseErrorHandler = Class.create();
AutonomousDatabaseErrorHandler.prototype = {
    initialize: function() {},

    documentErrorHandler: function(responseBody, status_code) {
        var msg = [];
        try {
            var responseJson = JSON.parse(responseBody);
            msg[0] = 'Code: ' + responseJson.code + ' - ' + status_code + ' ' + responseJson.message;
            msg[1] = 'Error';
        } catch (ex) {
            msg[0] = 'Issue in parsing Error response retrieved from API';
            msg[1] = 'Error';
        }
        return msg;
    },

    type: 'AutonomousDatabaseErrorHandler'
};
